export const addToCart = (item) => ({
    type: 'ADD_TO_CART',
    payload: item,
  });
  
  export const removeFromCart = (id) => ({
    type: 'REMOVE_FROM_CART',
    payload: id,
  });
  
  export const incrementItem = (id) => ({
    type: 'INCREMENT_ITEM',
    payload: id,
  });
  
  export const decrementItem = (id) => ({
    type: 'DECREMENT_ITEM',
    payload: id,
  });
