import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addToCart } from '../redux/actions';
import './ProductListingPage.css';

const products = [
  { id: 1, name: 'Snake Plant', price: 15, category: 'Air Purifying', thumbnail: 'snake-plant.jpg' },
  { id: 2, name: 'Spider Plant', price: 12, category: 'Air Purifying', thumbnail: 'spider-plant.jpg' },
  { id: 3, name: 'Peace Lily', price: 18, category: 'Air Purifying', thumbnail: 'peace-lily.jpg' },
  { id: 4, name: 'Boston Fern', price: 20, category: 'Aromatic Fragrant', thumbnail: 'boston-fern.jpg' },
  { id: 5, name: 'Rosemary', price: 15, category: 'Aromatic Fragrant', thumbnail: 'rosemary.jpg' },
  { id: 6, name: 'Aloe Vera', price: 22, category: 'Aromatic Fragrant', thumbnail: 'aloe-vera.jpg' },
];

const ProductListingPage = () => {
  const dispatch = useDispatch();
  const cart = useSelector(state => state.cart);

  const handleAddToCart = (product) => {
    dispatch(addToCart(product));
  };

  return (
    <div className="product-listing-page">
      <h2>Air Purifying Plants</h2>
      <div className="products">
        {products.filter(p => p.category === 'Air Purifying').map(product => (
          <div key={product.id} className="product-card">
            <img src={product.thumbnail} alt={product.name} />
            <h3>{product.name}</h3>
            <p>${product.price}</p>
            <button onClick={() => handleAddToCart(product)} disabled={cart.items.find(item => item.id === product.id)}>Add to Cart</button>
          </div>
        ))}
      </div>
      <h2>Aromatic Fragrant Plants</h2>
      <div className="products">
        {products.filter(p => p.category === 'Aromatic Fragrant').map(product => (
          <div key={product.id} className="product-card">
            <img src={product.thumbnail} alt={product.name} />
            <h3>{product.name}</h3>
            <p>${product.price}</p>
            <button onClick={() => handleAddToCart(product)} disabled={cart.items.find(item => item.id === product.id)}>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductListingPage;
