// src/redux/actions.js

export const addToCart = (product) => {
  return {
    type: 'ADD_TO_CART',
    payload: product
  };
};

export const removeFromCart = (productId) => {
  return {
    type: 'REMOVE_FROM_CART',
    payload: productId
  };
};

export const incrementItem = (productId) => {
  return {
    type: 'INCREMENT_ITEM',
    payload: productId
  };
};

export const decrementItem = (productId) => {
  return {
    type: 'DECREMENT_ITEM',
    payload: productId
  };
};
