// src/components/ShoppingCartPage.js

import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { removeFromCart, incrementItem, decrementItem } from '../redux/actions';
import './ShoppingCartPage.css';

const ShoppingCartPage = () => {
  const dispatch = useDispatch();
  const cartItems = useSelector(state => state.cart.items);

  const handleRemoveFromCart = (productId) => {
    dispatch(removeFromCart(productId));
  };

  const handleIncrementItem = (productId) => {
    dispatch(incrementItem(productId));
  };

  const handleDecrementItem = (productId) => {
    dispatch(decrementItem(productId));
  };

  const total = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);

  return (
    <div className="shopping-cart-page">
      <h2>Shopping Cart</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        cartItems.map(item => (
          <div className="cart-item" key={item.id}>
            <img src={`./images/${item.name.replace(/\s+/g, '-').toLowerCase()}.jpg`} alt={item.name} />
            <div className="cart-item-info">
              <h3>{item.name}</h3>
              <p>Price: ${item.price}</p>
              <p>Quantity: {item.quantity}</p>
              <div className="cart-item-actions">
                <button onClick={() => handleIncrementItem(item.id)}>+</button>
                <button onClick={() => handleDecrementItem(item.id)} disabled={item.quantity <= 1}>-</button>
                <button onClick={() => handleRemoveFromCart(item.id)}>Remove</button>
              </div>
            </div>
          </div>
        ))
      )}
      {cartItems.length > 0 && (
        <div className="cart-summary">
          <p>Total: ${total.toFixed(2)}</p>
          <button>Checkout</button>
        </div>
      )}
    </div>
  );
};

export default ShoppingCartPage;
